mapper.select:
select o.*,p.id pid,p.examine_record_id recordid,p.examine_item,p.result,p.result_destription
from examine_record o,examine_record_detail p where o.id = p.examine_record_id

  and o.examine_time < #{endTime}


  and o.examine_time BETWEEN #{startTime} and #{endTime}


  and o.examine_time > #{startTime}


  and o.district_id like '${district}%'


  and o.make_department_name like '%${makedepartmentName}%'
