mapper.select:
select 

from organ_police
where id = #{id,jdbcType=CHAR}
  and police_code = #{policeCode,jdbcType=CHAR}

mapper.update:
UPDATE  record_police
set is_delete = 1
where id = #{id,jdbcType=CHAR}

update record_police
set police_name = #{policeName,jdbcType=VARCHAR},
  telphone = #{telphone,jdbcType=VARCHAR},
  office_district = #{officeDistrict,jdbcType=VARCHAR},
  office_code = #{officeCode,jdbcType=VARCHAR},
  office_name = #{officeName,jdbcType=VARCHAR}
where id = #{id,jdbcType=CHAR}
  and police_code = #{policeCode,jdbcType=CHAR}
