mapper.update:
UPDATE use_department
SET is_delete = TRUE
where id= #{id}

UPDATE use_department
SET is_delete = TRUE,department_status = '02'
where id= #{id}

mapper.select:
 SELECT count(id) from use_department as a RIGHT JOIN(
select flag,MAX(version) as ver from use_department GROUP BY flag
)as b on a.flag=b.flag and  a.version=b.ver where district_id like '${districtId}%' and date_format(start_date,'%Y-%m')=date_format(now(),'%Y-%m') AND department_status = "01"

